const M = mut({});
document.M = M;
const defer = (fn, t = 200) => setTimeout(fn, t)

let m_editor 
function poll_editor(fn, interval = 150, count = 0){
  defer(function() {
    let E = M.EDITOR
    console.log('polled:',E)
	if (!E) poll_editor(fn, interval, count + 1)
    else {
      m_editor = E;
      console.log('editor after, ', count,': ', m_editor);
      fn()
   }
  }, interval)
}
const save = (path) =>  m_editor.state.save(path) 
