async function post(url = "", body) {
  try {
    let f = await fetch(url, {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(body)
    }).then((res) => res.json())
    return f
  }catch {
    return null
  }
}

